    def search_conversations(query, conversations_dir='./conversations', max_results=4):
        """Search through all conversations for relevant context"""
        import re

        if not os.path.exists(conversations_dir):
            return []

        results = []
        query_lower = query.lower()
        query_words = set(query_lower.split())

        try:
            for filename in os.listdir(conversations_dir):
                if filename.endswith('.jsonl'):
                    session_id = filename[:-6]
                    filepath = os.path.join(conversations_dir, filename)

                    try:
                        with open(filepath, 'r', encoding='utf-8') as f:
                            lines = list(f)

                        if not lines:
                            continue

                        # Get conversation title and metadata
                        first_line = json.loads(lines[0].strip())
                        conv_title = first_line.get('title', 'Untitled')
                        conv_date = first_line.get('timestamp', '')

                        # Search through messages
                        for i, line in enumerate(lines[1:], 1):  # Skip title line
                            try:
                                entry = json.loads(line.strip())
                                if 'role' not in entry or 'content' not in entry:
                                    continue

                                content = entry['content']
                                content_lower = content.lower()
                                role = entry['role']
                                timestamp = entry.get('timestamp', '')

                                # Calculate relevance score
                                score = 0

                                # Exact phrase match (highest score)
                                if query_lower in content_lower:
                                    score += 10

                                # Word matches
                                content_words = set(content_lower.split())
                                word_matches = len(query_words.intersection(content_words))
                                score += word_matches * 2

                                # Title relevance
                                if any(word in conv_title.lower() for word in query_words):
                                    score += 3

                                # Only include if there's some relevance
                                if score > 0:
                                    # Get surrounding context (previous and next message)
                                    context_messages = []

                                    def limit_to_tokens(text, max_tokens=50):
                                        """Limit text to approximately max_tokens (rough estimate: 1 token â 4 characters)"""
                                        words = text.split()
                                        if len(words) <= max_tokens:
                                            return text
                                        return ' '.join(words[:max_tokens]) + '...'

                                    # Previous message
                                    if i > 1:
                                        try:
                                            prev_entry = json.loads(lines[i-1].strip())
                                            if 'role' in prev_entry and 'content' in prev_entry:
                                                context_messages.append({
                                                    'role': prev_entry['role'],
                                                    'content': limit_to_tokens(prev_entry['content'])
                                                })
                                        except:
                                            pass

                                    # Current message
                                    context_messages.append({
                                        'role': role,
                                        'content': limit_to_tokens(content)
                                    })

                                    # Next message  
                                    if i < len(lines) - 1:
                                        try:
                                            next_entry = json.loads(lines[i+1].strip())
                                            if 'role' in next_entry and 'content' in next_entry:
                                                context_messages.append({
                                                    'role': next_entry['role'],
                                                    'content': limit_to_tokens(next_entry['content'])
                                                })
                                        except:
                                            pass

                                    results.append({
                                        'session_id': session_id,
                                        'conversation_title': conv_title,
                                        'conversation_date': conv_date,
                                        'matched_message': {
                                            'role': role,
                                            'content': content,
                                            'timestamp': timestamp
                                        },
                                        'context_messages': context_messages,
                                        'relevance_score': score
                                    })

                            except Exception as e:
                                continue

                    except Exception as e:
                        print(f"Error searching conversation {filename}: {e}")
                        continue

        except Exception as e:
            print(f"Error during conversation search: {e}")

        # Sort by relevance score and limit results
        results.sort(key=lambda x: x['relevance_score'], reverse=True)
        return results[:max_results]


